.settings-view .package-readme {
  font-size: 1.25em;
}
.settings-view .package-readme h1 {
  font-size: 2em;
  margin-top: 1.5em;
}
.settings-view .package-readme h2 {
  font-size: 1.75em;
  margin-top: 1.5em;
}
.settings-view .package-readme h3 {
  font-size: 1.5em;
  margin-top: 1.75em;
}
.settings-view .package-readme h4 {
  font-size: 1.25em;
  margin-top: 1.25em;
}
.settings-view .package-readme h1,
.settings-view .package-readme h2,
.settings-view .package-readme h3,
.settings-view .package-readme h4 {
  color: #d5d9e1;
}
.settings-view .package-readme a {
  color: #6494ed;
}
.settings-view .package-readme hr {
  border-color: rgba(154, 164, 182, 0.6);
}
.settings-view .package-readme blockquote {
  border-left-color: rgba(154, 164, 182, 0.6);
}
.settings-view .package-readme pre {
  margin-bottom: 1em;
}
.settings-view .package-readme img {
  max-width: 100%;
}
.settings-view .package-readme table {
  display: block;
  margin-bottom: 1em;
  width: 100%;
  overflow: auto;
  word-break: normal;
}
.settings-view .package-readme table th {
  font-weight: bold;
  text-align: center;
}
.settings-view .package-readme table th,
.settings-view .package-readme table td {
  padding: 6px 13px;
  border: 1px solid #1a1d24;
}
.settings-view .package-readme table tr {
  border-top: 1px solid #1a1d24;
  background-color: #2b303b;
}
.settings-view .package-readme table tr:nth-child(2n) {
  background-color: #333a47;
}
.settings-view .package-readme kbd {
  display: inline-block;
  padding: .3em .4em;
  font-size: .8em;
  line-height: 1;
  color: #d5d9e1;
  vertical-align: middle;
  background-color: #404859;
  border: solid 1px #1a1d24;
  border-bottom-color: #040506;
  border-radius: 3px;
  box-shadow: inset 0 -1px 0 #040506;
}
