.github-DialogPrompt {
  margin: 10px;
  text-align: center;
  font-size: 1.2em;
  user-select: none;
}
.github-DialogForm {
  display: flex;
  flex-direction: column;
  padding: 5px;
}
.github-DialogLabel {
  flex: 1;
  margin: 5px;
  position: relative;
  line-height: 2;
}
.github-DialogLabel--horizontal {
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  align-items: center;
}
.github-DialogLabel--horizontal input {
  margin: 0 10px;
}
.github-DialogLabel--horizontal input[type="text"],
.github-DialogLabel--horizontal input[type="password"] {
  width: 85%;
}
.github-DialogLabel .github-AtomTextEditor-container {
  margin-top: 5px;
}
.github-DialogFooter {
  display: flex;
  flex-direction: row;
  align-items: center;
}
.github-DialogInfo {
  display: inline-block;
  flex-grow: 1;
  margin: 5px;
  padding: 5px;
}
.github-DialogInfo li {
  display: inline-block;
}
.github-DialogButtons {
  flex-grow: 0;
  display: flex;
  align-items: center;
  justify-content: flex-end;
  padding: 5px;
}
.platform-win32 .github-DialogButtons {
  flex-direction: row-reverse;
  justify-content: flex-start;
}
.github-DialogButtons .btn {
  margin: 5px;
}
.github-DialogButtons-leftItem,
.github-DialogButtons-leftItem.btn {
  margin: 5px;
  margin-right: auto;
}
.github-Dialog--insetButton {
  position: absolute;
  background: transparent;
  right: 1em;
  top: 0;
  bottom: 0;
  border: none;
  color: rgba(154, 164, 182, 0.6);
  cursor: pointer;
}
.github-Dialog--insetButton:hover {
  color: #d5d9e1;
}
.github-Dialog--insetButton:focus {
  border: solid 1px #4d78cc;
  border-radius: 4px;
}
.github-DialogSpinner {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 10px;
}
.github-DialogSpinner .loading {
  margin: 10px;
}
.github-Dialog-row {
  display: flex;
  flex-direction: row;
  align-items: stretch;
}
.github-Dialog atom-text-editor[mini].editor {
  margin: 0;
}
.github-Dialog atom-text-editor[mini].editor[readOnly] {
  color: rgba(154, 164, 182, 0.6);
}
.github-Dialog:not(:focus-within) {
  padding-bottom: 1px;
  transition: padding-bottom 0.01s;
}
.github-TabbableWrapper {
  display: flex;
  flex: 1;
}
.github-RepositoryHome {
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
  width: 100%;
}
.github-RepositoryHome-owner {
  flex: 1;
}
.github-RepositoryHome-owner .Select-control {
  border-color: #1a1d24;
}
.github-RepositoryHome-owner.Select--single > .Select-control .Select-value.Select-value {
  background: #373f4d;
}
.github-RepositoryHome-owner.Select.has-value.Select--single > .Select-control .Select-value .Select-value-label {
  color: #9aa4b6;
}
.github-RepositoryHome-owner .Select-option {
  background: #373f4d;
  color: #9aa4b6;
}
.github-RepositoryHome-owner .Select-option.is-focused {
  background: #4d78cc;
}
.github-RepositoryHome-owner .Select-option.is-disabled {
  color: rgba(154, 164, 182, 0.6);
}
.github-RepositoryHome-ownerOption {
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
}
.github-RepositoryHome-ownerAvatar {
  flex: 0;
  border-radius: 2.5px;
  margin: 0 10px 0 0;
}
.github-RepositoryHome-ownerUnwritable {
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  align-items: center;
}
.github-RepositoryHome-separator {
  flex: 0;
  margin: 0 10px;
}
.github-RepositoryHome .github-AtomTextEditor-container {
  flex: 1.5;
}
.github-RemoteConfiguration-details summary {
  cursor: default;
  user-select: none;
  margin: 10px 0;
}
.github-RemoteConfiguration-details summary:focus {
  border: solid 1px #4d78cc;
  border-radius: 4px;
}
.github-RemoteConfiguration-details main {
  padding-top: 10px;
}
.github-RemoteConfiguration-protocol {
  display: inline-flex;
  align-items: center;
}
.github-RemoteConfiguration-protocolHeading {
  display: inline-block;
  margin-right: 10px;
}
.github-RemoteConfiguration-protocolOption {
  display: inline-flex;
  align-items: center;
  padding: 2.5px;
  margin-right: 10px;
}
.github-RemoteConfiguration-protocolOption:focus-within {
  border: solid 1px #4d78cc;
  border-radius: 4px;
}
.github-RemoteConfiguration-sourceRemote {
  width: 60%;
}
.github-RemoteConfiguration-sourceRemote label {
  display: flex;
  align-items: center;
  width: 100%;
}
.github-RemoteConfiguration-sourceRemote label .github-AtomTextEditor-container {
  flex: 1;
  margin-left: 10px;
}
.github-Create-visibility {
  display: inline-flex;
  align-items: center;
}
.github-Create-visibilityHeading {
  display: inline-block;
  margin-right: 20px;
}
.github-Create-visibilityOption {
  display: inline-flex;
  align-items: center;
  padding: 2.5px;
  margin-right: 10px;
}
.github-Create-visibilityOption:focus-within {
  border: solid 1px #4d78cc;
  border-radius: 4px;
}
.github-Create-visibilityOption span::before {
  margin-left: 5px;
  margin-right: 5px;
}
