.github-IssueishDetailView {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background-color: #2b303b;
}
.github-IssueishDetailView-container {
  display: flex;
  flex-direction: column;
  height: 100%;
}
.github-IssueishDetailView-header {
  display: flex;
  align-items: center;
  padding: 15px 20px;
  border-bottom: 1px solid #1a1d24;
}
.github-IssueishDetailView-headerColumn.is-flexible {
  flex: 1;
  display: flex;
  flex-wrap: wrap;
}
.github-IssueishDetailView-headerRow {
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  margin: 2px 0;
}
.github-IssueishDetailView-headerRow.is-fullwidth {
  flex: 1 1 100%;
}
.github-IssueishDetailView-avatar {
  align-self: flex-start;
  margin-right: 10px;
}
.github-IssueishDetailView-title {
  margin: 0;
  font-size: 1.25em;
  font-weight: 500;
  line-height: 1.3;
  color: #d5d9e1;
}
.github-IssueishDetailView-title:hover,
.github-IssueishDetailView-title:focus {
  text-decoration: none;
}
.github-IssueishDetailView-headerBadge {
  margin-right: 10px;
  padding: 0 0.75em;
  line-height: 1.65em;
}
.github-IssueishDetailView-headerLink {
  color: rgba(154, 164, 182, 0.6);
}
.github-IssueishDetailView-headerStatus {
  margin-left: 10px;
}
.github-IssueishDetailView-checkoutButton {
  font-weight: 600;
  margin-left: 10px;
}
.github-IssueishDetailView-headerRefreshButton {
  display: inline-block;
  margin-right: 10px;
  color: rgba(154, 164, 182, 0.6);
  cursor: pointer;
}
.github-IssueishDetailView-headerRefreshButton::before {
  display: block;
  width: 16px;
  height: 16px;
  line-height: .9;
  margin-right: 0;
  text-align: center;
}
.github-IssueishDetailView-headerRefreshButton.refreshing::before {
  animation: github-IssueishDetailView-headerRefreshButtonAnimation 2s linear 30;
}
@keyframes github-IssueishDetailView-headerRefreshButtonAnimation {
  100% {
    transform: rotate(360deg);
  }
}
.github-IssueishDetailView-avatarImage {
  width: 40px;
  height: 40px;
  border-radius: 3px;
}
.github-IssueishDetailView-meta {
  color: rgba(154, 164, 182, 0.6);
}
.github-IssueishDetailView-metaAuthor {
  color: inherit;
}
.github-IssueishDetailView-footer {
  padding: 10px;
  text-align: center;
  border-top: 1px solid #1a1d24;
  background-color: #242932;
}
.github-IssueishDetailView-footerLink.icon {
  color: rgba(154, 164, 182, 0.6);
}
.github-IssueishDetailView-footerLink.icon:before {
  vertical-align: -2px;
}
.github-IssueishDetailView-overview {
  max-width: 60em;
  margin: 0 auto;
}
.github-IssueishDetailView-overview > .github-DotComMarkdownHtml {
  padding: 20px;
}
.github-IssueishDetailView .github-EmojiReactions {
  padding: 0 20px 10px 20px;
}
.github-IssueishDetailView-buildStatus {
  padding: 20px;
}
.github-IssueishDetailView-buildStatus:empty {
  display: none;
}
.github-IssueishDetailView .github-PrCommitsView-commitWrapper {
  padding: 20px;
}
.github-IssueishDetailView-issueBody {
  flex: 1;
  overflow: auto;
}
.github-IssueishDetailView-issueBody > .github-DotComMarkdownHtml {
  padding: 20px;
}
