.github-IssueishList-item {
  margin: 2.5px;
}
.github-IssueishList-item--avatar {
  border-radius: 3px;
  height: 16px;
  width: 16px;
}
.github-IssueishList-item--title {
  flex: 1;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
}
.github-IssueishList-item--number {
  font-size: .9em;
  color: rgba(154, 164, 182, 0.6);
}
.github-IssueishList-item--status {
  text-align: center;
}
.github-IssueishList-item--status:before {
  margin-right: 0;
  width: 20px;
}
.github-IssueishList-item--status.icon-check {
  color: #73c990;
}
.github-IssueishList-item--status.icon-x {
  color: #ff6347;
}
.github-IssueishList-item--status.icon-dash {
  color: rgba(154, 164, 182, 0.6);
}
svg.github-IssueishList-item--status {
  height: 20px;
  width: 20px;
}
svg.github-IssueishList-item--status circle {
  cx: 10px;
  cy: 10px;
  r: 5.5;
  stroke-width: 3;
}
.github-IssueishList-item--age {
  color: rgba(154, 164, 182, 0.6);
  font-size: .9em;
  margin-left: 0;
  min-width: 3ch;
  text-align: center;
}
.github-IssueishList-item--menu {
  color: rgba(154, 164, 182, 0.6);
  line-height: 1;
}
.github-IssueishList-more {
  padding: 5px;
  display: flex;
  flex-direction: row;
  justify-content: center;
  border-top: 1px solid #1a1d24;
}
.github-IssueishList-more a {
  font-style: italic;
  color: rgba(154, 164, 182, 0.6);
}
.github-IssueishList-loading {
  padding: 5px;
  min-height: 29px;
  font-style: italic;
  text-align: center;
  color: rgba(154, 164, 182, 0.6);
}
.github-CreatePullRequestTile-message,
.github-CreatePullRequestTile-controls {
  padding: 10px;
  text-align: center;
  margin-bottom: 0;
}
.github-CreatePullRequestTile-message + .github-CreatePullRequestTile-controls {
  border-top: 1px solid #1a1d24;
}
.github-CreatePullRequestTile-message .icon {
  vertical-align: middle;
}
.github-CreatePullRequestTile-message .icon:before {
  width: auto;
}
.github-CreatePullRequestTile-divider {
  margin: 8px 20%;
  border-color: #1a1d24;
}
.github-CreatePullRequestTile-createPr {
  width: 100%;
}
.github-QueryErrorTile {
  padding: 10px;
  display: flex;
  color: #ff6347;
}
.github-QueryErrorTile-messages {
  display: flex;
  flex-direction: column;
  flex-grow: 1;
}
.github-QueryErrorTile .icon-alert {
  vertical-align: text-top;
}
.github-QueryErrorTile-message {
  font-weight: bold;
}
