.github-PrCommitView-container {
  display: flex;
  align-items: center;
  padding: 10px;
  font-size: 12px;
  border: 1px solid #1a1d24;
  border-bottom: none;
}
.github-PrCommitView-container:first-child {
  border-top-left-radius: 3px;
  border-top-right-radius: 3px;
}
.github-PrCommitView-container:last-child {
  border-bottom: 1px solid #1a1d24;
  border-bottom-left-radius: 3px;
  border-bottom-right-radius: 3px;
}
.github-PrCommitView-commit {
  flex: 1;
}
.github-PrCommitView-messageHeadline.is-button {
  padding: 0;
  margin-right: 6.66666667px;
  background-color: transparent;
  border: none;
  cursor: pointer;
  text-align: left;
  color: #d5d9e1;
}
.github-PrCommitView-messageHeadline.is-button:hover,
.github-PrCommitView-messageHeadline.is-button:focus {
  color: rgba(196, 201, 212, 0.8);
}
.github-PrCommitView-title {
  margin: 0 0 .25em 0;
  font-size: 1.2em;
  line-height: 1.4;
}
.github-PrCommitView-avatar {
  border-radius: 3px;
  height: 16px;
  margin-right: .4em;
  width: 16px;
}
.github-PrCommitView-metaText {
  line-height: 16px;
  vertical-align: middle;
  color: rgba(154, 164, 182, 0.6);
}
.github-PrCommitView-moreButton {
  margin: 0 auto 0 10px;
  padding: 0em .2em;
  color: rgba(154, 164, 182, 0.6);
  font-style: italic;
  font-size: .8em;
  border: 1px solid #1a1d24;
  border-radius: 3px;
  background-color: #373f4d;
}
.github-PrCommitView-moreButton:hover {
  background-color: #3c4453;
}
.github-PrCommitView-moreText {
  margin-top: 5px;
  padding: 0;
  font-size: inherit;
  font-family: var(--editor-font-family);
  word-wrap: initial;
  word-break: break-word;
  white-space: initial;
  background-color: transparent;
}
.github-PrCommitView-sha {
  margin-left: 10px;
  line-height: 16px;
  vertical-align: middle;
  color: #6494ed;
  font-family: var(--editor-font-family);
}
.github-PrCommitView-sha a {
  color: inherit;
}
