.github-PrTimeline {
  position: relative;
  border-top: 1px solid #3b424e;
}
.github-PrTimeline .timeline-item {
  padding: 15px 20px;
  padding-left: 45px;
  border-top: 1px solid #3b424e;
}
.github-PrTimeline .emoji,
.github-PrTimeline g-emoji {
  margin-right: .25em;
}
.github-PrTimeline .author-avatar {
  width: 20px;
  height: 20px;
  margin-right: 5px;
  border-radius: 3px;
}
.github-PrTimeline .open-commit-detail-button {
  padding: 0;
  background-color: transparent;
  border: none;
}
.github-PrTimeline .open-commit-detail-button:hover,
.github-PrTimeline .open-commit-detail-button:focus {
  color: #d5d9e1;
}
.github-PrTimeline .open-commit-detail-button:active {
  color: rgba(154, 164, 182, 0.6);
}
.github-PrTimeline .pre-timeline-item-icon {
  position: absolute;
  margin-left: -25px;
  text-align: center;
  margin-top: 2px;
  color: #4f5664;
  border-radius: 20px;
}
.github-PrTimeline .commits .info-row {
  margin-bottom: 10px;
}
.github-PrTimeline .commit {
  display: table-row;
  align-items: center;
  margin-left: 20px;
}
.github-PrTimeline .commit:first-child {
  padding-top: 10px;
}
.github-PrTimeline .commit:only-child {
  padding-top: 0;
  padding-bottom: 0;
  margin-left: -10px;
}
.github-PrTimeline .commit + .issue {
  margin-top: 5px;
}
.github-PrTimeline .commit .commit-author {
  position: absolute;
  display: flex;
}
.github-PrTimeline .commit .author-avatar {
  position: relative;
  margin-right: -16px;
  width: 20px;
  height: 20px;
  border-radius: 3px;
  background-color: #3b424e;
  box-shadow: 1px 0 #2b303b;
  transition: margin 0.12s cubic-bezier(0.5, 0.1, 0, 1);
}
.github-PrTimeline .commit .author-avatar:nth-child(1) {
  z-index: 3;
}
.github-PrTimeline .commit .author-avatar:nth-child(2) {
  z-index: 2;
  opacity: .7;
}
.github-PrTimeline .commit .author-avatar:nth-child(3) {
  z-index: 1;
  opacity: .4;
}
.github-PrTimeline .commit .author-avatar:nth-child(n+4) {
  display: none;
}
.github-PrTimeline .commit .author-avatar:only-child,
.github-PrTimeline .commit .author-avatar:last-child {
  box-shadow: none;
}
.github-PrTimeline .commit .commit-author:hover .author-avatar {
  display: inline-block;
  margin-right: 1px;
  opacity: 1;
}
.github-PrTimeline .commit .commit-message-headline {
  display: table-cell;
  line-height: 24px;
  padding: 0 3.33333333px;
  padding-left: 30px;
  max-height: 20px;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
  max-width: 0;
  width: 100%;
}
.github-PrTimeline .commit .commit-sha {
  display: table-cell;
  text-align: right;
  font-family: monospace;
  padding-left: 5px;
  color: #6494ed;
}
.github-PrTimeline .comment-message-header {
  color: rgba(154, 164, 182, 0.6);
}
.github-PrTimeline .comment-message-header a {
  color: inherit;
}
.github-PrTimeline .cross-referenced-events {
  border-spacing: 0 5px;
}
.github-PrTimeline .cross-referenced-events .info-row {
  margin-bottom: 10px;
}
.github-PrTimeline .cross-referenced-event {
  display: table-row;
}
.github-PrTimeline .cross-referenced-event:last-child {
  margin-bottom: 0;
}
.github-PrTimeline .cross-referenced-event-header {
  color: rgba(154, 164, 182, 0.6);
}
.github-PrTimeline .cross-referenced-event-label {
  display: table-cell;
  width: 100%;
}
.github-PrTimeline .cross-referenced-event-label-title {
  font-weight: bold;
}
.github-PrTimeline .cross-referenced-event-label-number {
  display: inline;
  margin-left: 5px;
  white-space: nowrap;
  color: rgba(154, 164, 182, 0.6);
}
.github-PrTimeline .cross-referenced-event-private {
  display: table-cell;
}
.github-PrTimeline .cross-referenced-event-state {
  display: table-cell;
  text-align: right;
}
.github-PrTimeline .cross-referenced-event-state .badge {
  margin-left: 5px;
}
.github-PrTimeline .sha {
  font-family: monospace;
}
.github-PrTimeline .merged-event {
  display: flex;
  align-items: center;
  font-size: .9em;
  line-height: 1.3;
  color: rgba(154, 164, 182, 0.6);
}
.github-PrTimeline .merged-event .username,
.github-PrTimeline .merged-event .merge-ref {
  font-weight: bold;
  color: #d5d9e1;
}
.github-PrTimeline .merged-event ~ .issue {
  border-bottom-style: dashed;
}
.github-PrTimeline .head-ref-force-pushed-event .username {
  font-weight: bold;
  color: #d5d9e1;
}
.github-PrTimeline .head-ref-force-pushed-event .sha {
  font-weight: bold;
}
.github-PrTimeline .issue .info-row {
  margin-bottom: 6.66666667px;
}
.github-PrTimeline .issue .github-DotComMarkdownHtml pre > code {
  white-space: pre;
}
.github-PrTimeline-loadMore {
  margin: 20px auto 0;
  text-align: center;
}
