.github-RecentCommits {
  max-height: 89px;
  overflow-x: hidden;
  overflow-y: auto;
  border-top: 1px solid #1a1d24;
}
.github-RecentCommits-list {
  margin: 0;
  padding: 5px 0;
  list-style: none;
}
.github-RecentCommits-message {
  padding: 5px 10px;
  text-align: center;
  color: rgba(154, 164, 182, 0.6);
}
.github-RecentCommit {
  position: relative;
  display: flex;
  align-items: center;
  padding: 0 10px;
  line-height: 26px;
}
.github-RecentCommit-authors {
  position: absolute;
  display: flex;
}
.github-RecentCommit-avatar {
  position: relative;
  margin-right: -13px;
  width: 16px;
  height: 16px;
  border-radius: 3px;
  color: transparent;
  background-color: #3b424e;
  box-shadow: 1px 0 #2b303b;
  overflow: hidden;
  transition: margin 0.12s cubic-bezier(0.5, 0.1, 0, 1);
}
.github-RecentCommit-avatar:nth-child(1) {
  z-index: 3;
}
.github-RecentCommit-avatar:nth-child(2) {
  z-index: 2;
  opacity: .7;
}
.github-RecentCommit-avatar:nth-child(3) {
  z-index: 1;
  opacity: .4;
}
.github-RecentCommit-avatar:nth-child(n+4) {
  display: none;
}
.github-RecentCommit-avatar:only-child,
.github-RecentCommit-avatar:last-child {
  box-shadow: none;
}
.github-RecentCommit-authors:hover .github-RecentCommit-avatar {
  display: inline-block;
  margin-right: 1px;
  opacity: 1;
}
.github-RecentCommit-message {
  flex: 1;
  margin: 0 .5em;
  margin-left: 26px;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
}
.github-RecentCommit-undoButton.btn {
  padding: 0 6.66666667px;
  margin-right: 5px;
  height: 20px;
  line-height: 1;
  font-size: .9em;
}
.github-RecentCommit-time {
  color: rgba(154, 164, 182, 0.6);
}
.github-RecentCommit:hover {
  color: #d5d9e1;
  background: #333a47;
}
.github-RecentCommit.is-selected {
  color: white;
  background: #3c4453;
}
.github-RecentCommits:focus .github-RecentCommit.is-selected {
  color: #ffffff;
  background: #4d78cc;
}
@media (min-height: 900px) {
  .github-RecentCommits {
    max-height: 141px;
  }
}
@media (min-height: 1200px) {
  .github-RecentCommits {
    max-height: 271px;
  }
}
