.about {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  -webkit-user-select: none;
  cursor: default;
  overflow: auto;
  text-align: center;
  font-size: 1.25em;
  line-height: 1.4;
  padding: 4em;
  color: #9aa4b6;
  background-color: #2b303b;
}
.about button {
  cursor: default;
}
.about a:focus {
  color: inherit;
}
.about img,
.about a {
  -webkit-user-drag: none;
}
.about .input-checkbox {
  margin-top: -0.2em;
}
.about .group-start {
  margin-top: 4em;
}
.about .group-item {
  margin-top: 1.5em;
}
.about-container {
  width: 100%;
  max-width: 500px;
}
.about-atom-io:hover .about-logo {
  color: #40a977;
}
.about-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 1em auto;
  color: #d5d9e1;
  transition: color 0.2s;
}
.about-version-container:hover {
  color: #c7ccd6;
}
.about-version-container:active {
  color: #f3f4f6;
}
.about-version {
  margin-right: .5em;
  font-size: 1.25em;
  vertical-align: middle;
}
.about-more-version {
  color: rgba(154, 164, 182, 0.6);
  font-size: .9em;
}
.about-header-release-notes {
  vertical-align: middle;
  margin-left: 1em;
}
.about-updates {
  width: 100%;
  max-width: 500px;
}
.about-updates-box {
  display: flex;
  align-items: center;
  padding: 10px;
  border: 1px solid #1a1d24;
  border-radius: 6px;
  background-color: #333a47;
}
.about-updates-status {
  flex: 1;
  margin-left: .5em;
  text-align: left;
}
.about-updates-item,
.about-default-update-message .about-updates-label {
  display: block;
}
.about-updates-label {
  color: rgba(154, 164, 182, 0.6);
}
.about-updates-label.is-strong {
  color: #9aa4b6;
}
.about-updates-version {
  margin: 0 .4em;
}
.about-updates-release-notes,
.about-updates-instructions {
  margin: 0 1em 0 1.5em;
}
.about-auto-updates {
  margin-top: 1em;
}
.about-auto-updates input {
  margin-right: .5em;
}
.about-love .icon::before {
  position: relative;
  width: auto;
  height: auto;
  margin-right: 0;
  font-size: 1.5em;
  vertical-align: text-top;
}
.about-love .icon-logo-github::before {
  font-size: 3.6em;
  height: .36em;
}
.about-credits {
  color: rgba(154, 164, 182, 0.6);
}
.about-release-notes {
  color: #0f82e6;
}
.about-release-notes:hover {
  color: #4ea6f4;
}
