.timecop {
  overflow: auto;
}
.timecop .timecop-panel {
  background-color: #242932;
}
.timecop .panels {
  display: flex;
  flex-direction: row;
}
.timecop .package-panel {
  display: flex;
  flex-direction: row;
  flex: 1;
}
.timecop .package-panel .list-group {
  overflow: hidden;
}
.timecop .package-panel .inset-panel {
  width: 100%;
}
.timecop .text-info {
  margin-bottom: 10px;
}
.timecop .timing,
.timecop .list-item {
  display: flex;
  justify-content: space-between;
}
.timecop .timing .inline-block:last-child,
.timecop .list-item .inline-block:last-child {
  margin-right: 0;
}
.timecop .timing {
  margin-bottom: 10px;
}
.timecop .list-item {
  margin-top: 5px;
  margin-bottom: 5px;
}
.timecop .package {
  color: #9aa4b6;
}
.timecop .package:hover {
  text-decoration: none;
  color: white;
}
.timecop .timecop-line {
  flex: 1 1 0;
  margin-right: 10px;
  margin-top: .80em;
  border-top: 1px dashed rgba(154, 164, 182, 0.2);
}
.timecop .package:hover + .timecop-line {
  border-color: rgba(154, 164, 182, 0.6);
}
